import React, { useState, useEffect } from "react";
import axios from "axios";

export const Home = () => {
  const [gates, setGates] = useState();
  const [gate, setGate] = useState();
  const [cheapRoute, setCheapRoute] = useState();
  const [journeyData, setjourneyData] = useState();

  useEffect(() => {
    const apiUrl = `https://hstc-api.testing.keyholding.com/gates`;

    axios
      .get(apiUrl, {
        headers: { "x-api-key": "94962B9A-966C-43FC-8E1A-145DEAA5970C" },
      })
      .then((response) => {
        setGates(response.data);
      });
  }, []);

  const handleSelectedGate = (gateCode) => {
    const apiUrl = `https://hstc-api.testing.keyholding.com/gates/${gateCode}`;

    axios
      .get(apiUrl, {
        headers: { "x-api-key": "94962B9A-966C-43FC-8E1A-145DEAA5970C" },
      })
      .then((response) => {
        setGate(response.data);
      });
  };

  const getCheapRoutes = (gateCode, targetGateCode) => {
    const apiUrl = `https://hstc-api.testing.keyholding.com/gates/${gateCode}/to/${targetGateCode}`;

    axios
      .get(apiUrl, {
        headers: { "x-api-key": "94962B9A-966C-43FC-8E1A-145DEAA5970C" },
      })
      .then((response) => {
        setCheapRoute(response.data);
      });
  };

  const calculateJourneyCost = (distance) => {
    const apiUrl = `https://hstc-api.testing.keyholding.com/transport/${distance}`;

    axios
      .get(apiUrl, {
        headers: { "x-api-key": "94962B9A-966C-43FC-8E1A-145DEAA5970C" },
      })
      .then((response) => {
        setjourneyData(response.data);
      });
  };

  console.log(gates, "api data");
  console.log(gate, "selected gate");
  console.log(cheapRoute, "my cheap route");
  return (
    <div>
      <h1 onClick={() => handleSelectedGate("SOL")}>Home</h1>
      <p onClick={() => getCheapRoutes("SOL", "RAN")}>Cheap trip</p>
      <p onClick={() => calculateJourneyCost(100)}>Cost of journey</p>
    </div>
  );
};
